INSERT INTO `categorie` (`id_categorie`, `nom_categorie`, `description_categorie`, `image_default`) VALUES
(1, 'Racine', '', 0x2e2e2f696d616765732f726163696e652e6a7067),
(2, 'Boutique', '', 0x2e2e2f696d616765732f626f7574697175652e6a7067),
(10, 'Infusion', '<p><span style=\"color:#363a41;font-family:\'Open Sans\', Helvetica, Arial, sans-serif;font-size:12.8px;\">La Brûlerie Comtoise vous propose une sélection d\'infusions...<br />Classiques, fruitées, épicées il y en a pour tous les goûts... A consommer aussi bien chaudes que glacées elles sauront vous accompagner toute au long de la journée et au fil des saisons... </span></p>', 0x2e2e2f696d616765732f696e667573696f6e2e6a7067),
(11, 'Infusettes', '<p>La Brûlerie Comtoise vous fait découvrir sa large de gamme d\'infusions en sachet individuel. Au bureau comme à la maison, plus pratique et plus rapide, faites vous plaisir avec ces infusettes Made in France.</p>', 0x2e2e2f696d616765732f696e66757365747465732e6a7067),
(12, 'Infusion vrac', '<p>La Brûlerie Comtoise vous propose une sélection d\'infusions.<br />Classiques, fruitées, épicées il y en a pour tous les goûts... A consommer aussi bien chaudes que glacées elles sauront vous accompagner tout au long de la journée et au fil des saisons...</p>', 0x2e2e2f696d616765732f696e667573696f6e567261632e6a706567),
(13, 'Thé', 'Description de la catégorie thé', 0x2e2e2f696d616765732f7468652e6a7067),
(14, 'Café', '', 0x2e2e2f696d616765732f636166652e6a7067),
(15, 'Infusettes', '<p>La Brûlerie Comtoise vous fait découvrir sa large gamme de thés en sachets individuels. Au bureau comme à la maison, plus pratique et plus rapide, faites vous plaisir avec ces infusettes Made in France. </p>', 0x2e2e2f696d616765732f696e66757365747465732e6a7067),
(16, 'Thé vrac', '<p>Découvrez la sélection de thés vrac de la Brûlerie Comtoise. <br />Amateurs de grandes origines, de mélanges aromatisés, fruités ou épicés... Sur une base de thé blanc, de thé vert ou de thé noir... il y a de quoi ravir tous les palais...</p>', NULL),
(17, 'Café capsule', '<p style=\"text-align:right;\"><img src=\"https://www.labruleriecomtoise.fr/img/cms/capsules.jpg\" alt=\"\" width=\"275\" height=\"207\" style=\"float:left;\" /></p>\n<blockquote>\n<p style=\"text-align:justify;\">La Brûlerie Comtoise a développé sa sélection de capsules compatibles Nespresso®. Après avoir été torréfié le café est transformé et mis en capsule dans notre manufacture de la Grande rue de Dole. Étant basée sur la même gamme que notre sélection de café en grain, les capsules bénéficient des mêmes labels : Rainforest, issu de l\'agriculture biologique. De plus, elles sont labellisées « Made in Jura », label qui valorise la production artisanale et locale. </p>\n<p style=\"text-align:justify;\"></p>\n<p style=\"text-align:justify;\"></p>\n<p style=\"text-align:justify;\">La production se veut intégralement Made in France. La machine qui permet de concevoir les capsules, ainsi que les capsules elles-mêmes sont fabriquées en France. Élaborées dans des matières biodégradables, la dosette est en amidon de maïs et l\'opercule en canne à sucre. Elles illustrent pleinement la volonté de l\'entreprise de préserver l\'environnement et de limiter la production de déchets. </p>\n</blockquote>\n<p align=\"justify\" style=\"margin-bottom:0cm;text-align:justify;\"></p>', NULL),
(18, 'Café grain', '', NULL),
(19, 'Café grain & moulu', '<p style=\"text-align:justify;\">Le cœur du métier de la Brûlerie Comtoise est sa torréfaction 100% doloise. Du sourcing, à la réception des échantillons, aux séances de tasting et pour finir à la transformation du grain vert... tout se passe entre les quatre mûrs de la manufacture de la Grande rue de Dole. </p>\n<p style=\"text-align:justify;\">Attirés par les effluves du café grillé, les passants du centre ville peuvent assister aux séances de torréfaction et écouter les histoires et les conseils de Yoann sur le café. </p>\n<p style=\"text-align:justify;\">Torréfacteur et diplômé du Q Arabica Grader, Yoann Bride gère la sélection et la production du café de la Brûlerie Comtoise. Il choisit lui-même les producteurs avec lesquels il veut collaborer afin de veiller que leurs plantations leur assurent une rémunération correcte. Dans un souci de qualité, de préservation de l\'environnement le choix des grains se porte sur des origines labellisées. Issue de l\'agriculture biologique, labellisée Rainforest (préservation de la forêt) la gamme proposée se veut au plus près de la nature. </p>\n<p style=\"text-align:justify;\">La sélection de Yoann, sera un voyage à chaque tasse. Destination l\'Afrique avec un café d\'Éthiopie ou du Burundi. L\'Amérique du Sud avec le café aux notes chocolatées du Pérou ou de Colombie, suaves du Honduras, des notes de fruits rouges dans le Brésil ou le déca du Mexique. </p>\n<p style=\"text-align:justify;\">Le traditionnel Mélange Italien avec son assemblage d\'arabica et de robusta et le Blend de la Brûlerie entièrement élaboré par Yoann complètent la gamme. Vous y trouverez des cafés intenses, corsés, fruités, acidulés, gourmands, doux et fleuris. Palais de grand amateur, de débutant ou d\'épicurien qui aime les bonnes choses il y aura forcément un café pour vous. </p>\n<p style=\"text-align:justify;\">Nous vous proposons également un café expérimental, unique au monde, vieilli en fût de Sauternes 1er grand cru classé. Ce café issu du travail de 3 amis, Simon Verger, MOF Maître d\'hôtel et directeur du Skiff club, 2 étoilés Michelin, de Jean Compeyrot du Château Sigalas Rabaud, 1er grand cru classé et de notre torréfacteur Yoann.</p>', NULL),
(20, 'Rooibos', '<p>La Brûlerie Comtoise propose une gamme de rooibos parfumés. Issu d\'Afrique du Sud, faible en tanin, sans théine et aux vertus antioxydantes, le rooibos accompagnera volontiers toutes vos soirées. </p>', NULL),
(21, 'Accompagnements', '<p>Pour un moment de plaisir, pour accompagner votre café ou votre thé, la Brûlerie Comtoise a sélectionné pour vous une gamme de confiseries... à grignoter, à tartiner ou à déguster... </p>', NULL),
(22, 'Machine & Accessoire', '', NULL),
(23, 'Les Chocolats de Damien Benetot', '<p>Les Chocolats de Damien Benetot</p>\n<p>Pâtisserie Damien BENETOT</p>\n<p><a href=\"http://www.benetot.fr/\">http://www.benetot.fr/</a></p>\n<p>26 rue de Besançon</p>\n<p>39100 Dole</p>', NULL),
(24, 'Coffret', '', NULL),
(25, 'Pâques - Moulage', '<p>Les Chocolats de Damien Benetot</p>\n<p>Pâtisserie Damien BENETOT</p>\n<p><a href=\"http://www.benetot.fr/\">http://www.benetot.fr/</a></p>\n<p>26 rue de Besançon</p>\n<p>39100 Dole</p>', NULL),
(26, 'Chocolats, macarons et divers', '<p>Les Chocolats de Damien Benetot</p>\n<p>Pâtisserie Damien BENETOT</p>\n<p><a href=\"http://www.benetot.fr/\">http://www.benetot.fr/</a></p>\n<p>26 rue de Besançon</p>\n<p>39100 Dole</p>', NULL),
(27, 'Epicerie', '<p>Pour un moment de plaisir, pour accompagner votre café ou votre thé, la Brûlerie Comtoise a sélectionné pour vous une gamme Épicerie fine... à grignoter, à tartiner ou à déguster... </p>\n<p>Pour vous satisfaire du mieux possible nous avons élaboré une gamme de produits artisanaux, locaux si possible mais surtout MADE IN FRANCE. Nous avons à cœur de proposer des produits de qualité qui sauront accompagner vos instants de dégustation.</p>', NULL),
(28, 'Confiseries', '<p>Pour un moment de plaisir, pour accompagner votre café ou votre thé, la Brûlerie Comtoise a sélectionné pour vous une gamme de confiseries... à grignoter, à tartiner ou à déguster... </p>', NULL),
(29, 'Thé & Infusion', '', NULL),
(30, 'Vrac', '<p><span style=\"color:#ffffff;\">Découvrez la sélection de thés et infusions vrac de la Brûlerie Comtoise. </span></p>\n<p><span style=\"color:#ffffff;\">Amateurs de grandes origines, de mélanges aromatisés, fruités ou épicés... Sur une base de thé blanc, vert ou noir, de fruits ou de plantes ... Il y a de quoi ravir tous les palais ...  </span></p>', NULL);
